import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import './EarthquakeMap.css';

// Leaflet'in varsayılan ikon sorunu için çözüm
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

function EarthquakeMap({ earthquakes }) {
  const turkeyCenter = [39.0, 35.0]; // Türkiye'nin merkezi koordinatları

  return (
    <MapContainer center={turkeyCenter} zoom={6} className="map-container">
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      {earthquakes.map((quake, index) => (
        <Marker
          key={index}
          position={[quake.lat, quake.lng]}
        >
          <Popup>
            <div>
              <h3>{quake.title}</h3>
              <p><strong>Büyüklük:</strong> {quake.mag}</p>
              <p><strong>Derinlik:</strong> {quake.depth} km</p>
              <p><strong>Tarih:</strong> {new Date(quake.date).toLocaleString('tr-TR')}</p>
            </div>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
}

export default EarthquakeMap;
