import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './EarthquakeMap.css';

// Depremin büyüklüğüne göre farklı renkler kullanacağız
const getMarkerIcon = (magnitude) => {
  let color = '#48bb78'; // yeşil (küçük depremler)
  if (magnitude >= 4.0) color = '#ecc94b'; // sarı
  if (magnitude >= 5.0) color = '#f56565'; // kırmızı

  return L.divIcon({
    html: `
      <div style="
        background-color: ${color};
        width: 24px;
        height: 24px;
        border-radius: 12px;
        border: 2px solid white;
        box-shadow: 0 0 4px rgba(0,0,0,0.3);
        display: flex;
        align-items: center;
        justify-content: center;
        color: white;
        font-weight: bold;
        font-size: 12px;
      ">
        ${magnitude}
      </div>
    `,
    className: 'custom-div-icon',
    iconSize: [24, 24],
    iconAnchor: [12, 12],
    popupAnchor: [0, -12],
  });
};

function EarthquakeMap({ earthquakes }) {
  const turkeyCenter = [39.0, 35.0];

  return (
    <MapContainer center={turkeyCenter} zoom={6} className="map-container">
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      {earthquakes.map((quake, index) => (
        <Marker
          key={index}
          position={[quake.lat, quake.lng]}
          icon={getMarkerIcon(quake.mag)}
        >
          <Popup>
            <div>
              <h3>{quake.title}</h3>
              <p><strong>Büyüklük:</strong> {quake.mag}</p>
              <p><strong>Derinlik:</strong> {quake.depth} km</p>
              <p><strong>Tarih:</strong> {new Date(quake.date).toLocaleString('tr-TR')}</p>
            </div>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
}

export default EarthquakeMap;
